import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const CategoryPage = () => {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState({});
  const { categoryId } = useParams();

  useEffect(() => {
    const fetchCategoryDetails = async () => {
      setLoading(true);
      try {
        // In a real app, this would be API calls
        // const categoryResponse = await fetch(`/api/categories/${categoryId}`);
        // const categoryData = await categoryResponse.json();
        // const moviesResponse = await fetch(`/api/categories/${categoryId}/movies`);
        // const moviesData = await moviesResponse.json();
        
        // Placeholder data
        const categoryData = {
          id: categoryId,
          name: `Category ${categoryId}`,
          description: 'This is a sample category description.',
        };
        
        const placeholderMovies = [
          { id: 1, title: 'Sample Movie 1', year: 2023, poster: '/placeholder.jpg' },
          { id: 2, title: 'Sample Movie 2', year: 2022, poster: '/placeholder.jpg' },
          { id: 3, title: 'Sample Movie 3', year: 2021, poster: '/placeholder.jpg' },
          { id: 4, title: 'Sample Movie 4', year: 2020, poster: '/placeholder.jpg' },
        ];
        
        setTimeout(() => {
          setCategory(categoryData);
          setMovies(placeholderMovies);
          setLoading(false);
        }, 500);
      } catch (error) {
        console.error('Error fetching category data:', error);
        setLoading(false);
      }
    };

    fetchCategoryDetails();
  }, [categoryId]);

  if (loading) {
    return (
      <div className="container mt-5">
        <h2>Loading category...</h2>
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-12 mb-4">
          <h1>{category.name}</h1>
          <p className="lead">{category.description}</p>
        </div>
      </div>
      
      <div className="row">
        {movies.length === 0 ? (
          <div className="col-12">
            <p>No movies found in this category.</p>
          </div>
        ) : (
          movies.map((movie) => (
            <div key={movie.id} className="col-md-3 mb-4">
              <div className="card h-100">
                <img 
                  src={movie.poster} 
                  className="card-img-top" 
                  alt={movie.title}
                  onError={(e) => {
                    e.target.src = '/placeholder.jpg';
                  }}
                />
                <div className="card-body">
                  <h5 className="card-title">{movie.title}</h5>
                  <p className="card-text">{movie.year}</p>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CategoryPage;
